import React from "react";

export class Uploader extends React.Component {
    constructor(props) {
        super(props);
        this.canvasRef = React.createRef();
        this.imgRef = React.createRef();
        this.state = {file: '', imagePreviewUrl: ''};
    }

    componentDidMount() {
        const canvas = this.canvasRef
        const img = this.imgRef;
        const ctx = canvas.current.getContext("2d");
    }

    _handleSubmit(e) {
        e.preventDefault();
        // TODO: do something with -> this.state.file
        console.log('handle uploading-', this.state.file);
    }

    findPos(obj) {
        let curleft = 0, curtop = 0;
        if (obj.offsetParent) {
            do {
                curleft += obj.offsetLeft;
                curtop += obj.offsetTop;
            } while (obj = obj.offsetParent);
            return { x: curleft, y: curtop };
        }
        return undefined;
    }

    rgbToHex(r, g, b) {
        if (r > 255 || g > 255 || b > 255)
            throw "Invalid color component";
        return ((r << 16) | (g << 8) | b).toString(16);
    }

    _handleFindPosition(e) {
        const canvas = this.canvasRef
        const ctx = canvas.current.getContext("2d");
        var pos = this.findPos(canvas.current);
        var x = e.pageX - pos.x;
        var y = e.pageY - pos.y;
        // var coord = "x=" + x + ", y=" + y;
        var p = ctx.getImageData(x, y, 10, 10).data;
        var hex = "#" + ("000000" + this.rgbToHex(p[0], p[1], p[2])).slice(-6);
        console.log(hex)
    }

    _handleImageChange(e) {
        e.preventDefault();

        let reader = new FileReader();
        let file = e.target.files[0];

        reader.onloadend = (e) => {
            this.setState({
                file: file,
                imagePreviewUrl: reader.result
            });

            let ctx = this.canvasRef.current.getContext("2d");
            let i = new Image();
            i.src = e.target.result;
            ctx.drawImage(i, 0, 0, 240,240);
            console.log("load end");

        }
        reader.readAsDataURL(file)
    }

    render() {
        let {imagePreviewUrl} = this.state;
        let $imagePreview = null;
        if (imagePreviewUrl) {
            $imagePreview = (<img ref={this.imgRef} height={240} width={240} alt={"img"} src={imagePreviewUrl}/>);
            return (
                <div className="previewComponent">
                    <form onSubmit={(e) => this._handleSubmit(e)}>
                        <input className="fileInput"
                               type="file"
                               onChange={(e) => this._handleImageChange(e)}/>
                    </form>
                    <div className="imgPreview">
                        {$imagePreview}
                    </div>
                    <br/>
                    <h4>Canvas</h4>
                    <canvas
                        onMouseMove={(e) => this._handleFindPosition(e)}
                        ref={this.canvasRef} width={240} height={240} />
                </div>
            );
        } else {
            $imagePreview = (<div className="previewText">Please select an Image for Preview</div>);
            return (
                <div className="previewComponent">
                    <form onSubmit={(e) => this._handleSubmit(e)}>
                        <input className="fileInput"
                               type="file"
                               onChange={(e) => this._handleImageChange(e)}/>
                    </form>
                    <div className="imgPreview">
                        {$imagePreview}
                    </div>
                    <h4>Canvas</h4>
                    <canvas ref={this.canvasRef} width={240} height={240} />
                </div>
            );
        }
    }
}
